items.script:
fetch("rename-map.json")
  .then(r => r.json())
  .then(map => {
    let xml = "";
    for (const oldName in map) {
      const file = map[oldName];
      xml += `


          ${file.replace(".html", "")}
          https://chenbaiqiang30.github.io/my-page/${file}
          https://chenbaiqiang30.github.io/my-page/${file}

      `;
    }
    document.write(xml);
  });
